INSERT INTO `wp_icl_strings` VALUES("2098", "en", "default", "3b878279a04dc47d60932cb294d96259", "Overview", NULL, "LINE", NULL, "0", "", "222843468c38b7ce807f729c952750c3");
INSERT INTO `wp_icl_strings` VALUES("2099", "en", "default", "3929b55d6d5a486c2c71f53df11c0e3f", "Plugins extend and expand the functionality of WordPress. Once a plugin is installed, you may activate it or deactivate it here.", NULL, "LINE", NULL, "0", "", "83e4e8dfa6e1efae4ce164c016ab9d46");
INSERT INTO `wp_icl_strings` VALUES("2100", "en", "default", "dff9e48815ee70045ecc9844adef0f15", "The search for installed plugins will search for terms in their name, description, or author.", NULL, "LINE", NULL, "0", "", "26eb73c3c9d1249e7a8eb21aaa1aca1e");
INSERT INTO `wp_icl_strings` VALUES("2101", "en", "default", "6c13edf4e89b56c3fe70bb814280fafb", "The search results will be updated as you type.", NULL, "LINE", NULL, "0", "", "f80a59cb24e68ba6e40bf1bf9b0a7139");
INSERT INTO `wp_icl_strings` VALUES("2102", "en", "default", "ce99e8b4ddb1935ad216af170d465663", "If you would like to see more plugins to choose from, click on the &#8220;Add New&#8221; button and you will be able to browse or search for additional plugins from the <a href=\"%s\">WordPress Plugin Directory</a>. Plugins in the WordPress Plugin Directory are designed and developed by third parties, and are compatible with the license WordPress uses. Oh, and they&#8217;re free!", NULL, "LINE", NULL, "0", "", "918021f3c1c7333bbb985975acdf69b3");
INSERT INTO `wp_icl_strings` VALUES("2103", "en", "default", "fb70939c8ecbd4faff2338ee6c5d2529", "https://wordpress.org/plugins/", NULL, "LINE", NULL, "0", "", "9b586226e6bb056771f2640e81e62c10");
INSERT INTO `wp_icl_strings` VALUES("2104", "en", "default", "231cf4c70d866b616c21baddaeed0696", "Troubleshooting", NULL, "LINE", NULL, "0", "", "f15a54e254bfb229acb9aa82aac27ebe");
INSERT INTO `wp_icl_strings` VALUES("2105", "en", "default", "fcd99583341d36fed9a308df418b0501", "Most of the time, plugins play nicely with the core of WordPress and with other plugins. Sometimes, though, a plugin&#8217;s code will get in the way of another plugin, causing compatibility issues. If your site starts doing strange things, this may be the problem. Try deactivating all your plugins and re-activating them in various combinations until you isolate which one(s) caused the issue.", NULL, "LINE", NULL, "0", "", "f0345a79c1b5429286734217a80c98ed");
INSERT INTO `wp_icl_strings` VALUES("2106", "en", "default", "37a24e605a3cda57698e531f125740bc", "If something goes wrong with a plugin and you can&#8217;t use WordPress, delete or rename that file in the %s directory and it will be automatically deactivated.", NULL, "LINE", NULL, "0", "", "b9b8f473d110eff046e9836bde623ed6");
INSERT INTO `wp_icl_strings` VALUES("2107", "en", "default", "55707b4848b3a26a39057e3af6948b43", "For more information:", NULL, "LINE", NULL, "0", "", "a9d3d49c9ca9a29198e51259c1dc87e6");
