INSERT INTO `wp_icl_strings` VALUES("3479", "en", "woocommerce-multilingual", "c73ea3008e635a1f760932d8137cd4d9", "Troubleshooting page", NULL, "LINE", NULL, "0", "", "af465e6a165e126e1d3fb86493b8bae0");
INSERT INTO `wp_icl_strings` VALUES("3481", "en", "default", "5f71bda8d746f5a38f8be090639b5e2f", "Widgets are independent sections of content that can be placed into any widgetized area provided by your theme (commonly called sidebars). To populate your sidebars/widget areas with individual widgets, drag and drop the title bars into the desired area. By default, only the first widget area is expanded. To populate additional widget areas, click on their title bars to expand them.", NULL, "LINE", NULL, "0", "", "2628ac04367ef5339322e23a38240fb5");
INSERT INTO `wp_icl_strings` VALUES("3482", "en", "default", "554b7ac84f1fa573ee1867a3a2c02d09", "The Available Widgets section contains all the widgets you can choose from. Once you drag a widget into a sidebar, it will open to allow you to configure its settings. When you are happy with the widget settings, click the Save button and the widget will go live on your site. If you click Delete, it will remove the widget.", NULL, "LINE", NULL, "0", "", "c870042c568c84af431c05479fe39260");
INSERT INTO `wp_icl_strings` VALUES("3483", "en", "default", "0eaaeaef7c7f3164d2ece6ec88571c19", "Removing and Reusing", NULL, "LINE", NULL, "0", "", "449caefc4319d4e355a74cfbc0831768");
INSERT INTO `wp_icl_strings` VALUES("3484", "en", "default", "fc276f47f9e88e53fb9a81e0e683a057", "If you want to remove the widget but save its setting for possible future use, just drag it into the Inactive Widgets area. You can add them back anytime from there. This is especially helpful when you switch to a theme with fewer or different widget areas.", NULL, "LINE", NULL, "0", "", "f5f6f5978f1a37ee120927289f3f6f8d");
INSERT INTO `wp_icl_strings` VALUES("3485", "en", "default", "2a1b56a60634250cd6cf205c74ed00c6", "Widgets may be used multiple times. You can give each widget a title, to display on your site, but it&#8217;s not required.", NULL, "LINE", NULL, "0", "", "cb26d608dcaf5e53bfdccbb076ee7f6b");
INSERT INTO `wp_icl_strings` VALUES("3486", "en", "default", "c53d7d249deb2d1f0a9f4aedc03dd2c3", "Enabling Accessibility Mode, via Screen Options, allows you to use Add and Edit buttons instead of using drag and drop.", NULL, "LINE", NULL, "0", "", "ac9278c82439b66854fc13fb321c96e5");
