INSERT INTO `wp_icl_strings` VALUES("3489", "en", "default", "7703983ef109c52a423492c4bfa5ffbe", "When changing themes, there is often some variation in the number and setup of widget areas/sidebars and sometimes these conflicts make the transition a bit less smooth. If you changed themes and seem to be missing widgets, scroll down on this screen to the Inactive Widgets area, where all of your widgets and their settings will have been saved.", NULL, "LINE", NULL, "0", "", "a4018b4b9fa0ad74ac664770d4fa25bd");
