INSERT INTO `wp_icl_strings` VALUES("4036", "en", "default", "6fac4cf7006e5f1e690aa854a4ce88cb", "This screen is used for managing your custom navigation menus.", NULL, "LINE", NULL, "0", "", "503b00512c77c1328df6dca8fde0b721");
INSERT INTO `wp_icl_strings` VALUES("4037", "en", "default", "1a5e8350b029491d95ee4ed176e0d985", "Menus can be displayed in locations defined by your theme, even used in sidebars by adding a &#8220;Custom Menu&#8221; widget on the <a href=\"%1$s\">Widgets</a> screen. If your theme does not support the custom menus feature (the default themes, %2$s and %3$s, do), you can learn about adding this support by following the Documentation link to the side.", NULL, "LINE", NULL, "0", "", "c6894d5c5a5eb73410be97f201633a51");
INSERT INTO `wp_icl_strings` VALUES("4038", "en", "default", "e55f8cc8fd22b7b8050487b7a3e72591", "From this screen you can:", NULL, "LINE", NULL, "0", "", "3611d1e9c61525efd74a0ca84d3e6a35");
INSERT INTO `wp_icl_strings` VALUES("4039", "en", "default", "62e8a03a8a795cc01cfdd5ac52f6af69", "Create, edit, and delete menus", NULL, "LINE", NULL, "0", "", "aa705ff9e55c838e9b36f76af42732dd");
INSERT INTO `wp_icl_strings` VALUES("4040", "en", "default", "a1a977f79034213b41473e18733730b4", "Add, organize, and modify individual menu items", NULL, "LINE", NULL, "0", "", "daf280a25ee23d58436487d5c31a9dd1");
INSERT INTO `wp_icl_strings` VALUES("4041", "en", "default", "076cce8a4cea590d426b0a923e9c6f21", "The menu management box at the top of the screen is used to control which menu is opened in the editor below.", NULL, "LINE", NULL, "0", "", "0360469d6dc114e3b72568de3416d133");
INSERT INTO `wp_icl_strings` VALUES("4042", "en", "default", "759e3687dbefb5670eb444bb69549e84", "To edit an existing menu, <strong>choose a menu from the drop down and click Select</strong>", NULL, "LINE", NULL, "0", "", "90ef2d63481fd79ab0c09c14d93bc812");
INSERT INTO `wp_icl_strings` VALUES("4043", "en", "default", "c5ecefc2c05db6570556c7385ac2e8a3", "If you haven&#8217;t yet created any menus, <strong>click the &#8217;create a new menu&#8217; link</strong> to get started", NULL, "LINE", NULL, "0", "", "e3750bb5eb2eb05b624dbab6c20b5e59");
INSERT INTO `wp_icl_strings` VALUES("4044", "en", "default", "72caeed1ff140d706e86977a0bf7d12c", "You can assign theme locations to individual menus by <strong>selecting the desired settings</strong> at the bottom of the menu editor. To assign menus to all theme locations at once, <strong>visit the Manage Locations tab</strong> at the top of the screen.", NULL, "LINE", NULL, "0", "", "9362d167fdacec13de259ffccbd99596");
INSERT INTO `wp_icl_strings` VALUES("4045", "en", "default", "1a12590b328706178ff2578fc839555c", "Menu Management", NULL, "LINE", NULL, "0", "", "3eedd223b76fd68e50248a0655649922");
INSERT INTO `wp_icl_strings` VALUES("4046", "en", "default", "61a1fba5abfdef97566c7b73d4ebf8b7", "Each custom menu may contain a mix of links to pages, categories, custom URLs or other content types. Menu links are added by selecting items from the expanding boxes in the left-hand column below.", NULL, "LINE", NULL, "0", "", "a98ddac16b17739ea2c8f12b56b26e5d");
INSERT INTO `wp_icl_strings` VALUES("4047", "en", "default", "2bc2b92ef21e54b7d57719af3e03b906", "<strong>Clicking the arrow to the right of any menu item</strong> in the editor will reveal a standard group of settings. Additional settings such as link target, CSS classes, link relationships, and link descriptions can be enabled and disabled via the Screen Options tab.", NULL, "LINE", NULL, "0", "", "44adfce5db156b9d67e7c275453a89b9");
INSERT INTO `wp_icl_strings` VALUES("4048", "en", "default", "1553376081dcd5ee16ee1f659503b995", "Add one or several items at once by <strong>selecting the checkbox next to each item and clicking Add to Menu</strong>", NULL, "LINE", NULL, "0", "", "5d76d4900fad4092324ff32d9f3614da");
INSERT INTO `wp_icl_strings` VALUES("4049", "en", "default", "938b9b0ac73de7852ecf9d552bb1ff17", "To add a custom link, <strong>expand the Custom Links section, enter a URL and link text, and click Add to Menu</strong>", NULL, "LINE", NULL, "0", "", "3e1d035dbae7e5513d9e82fed3a7b333");
INSERT INTO `wp_icl_strings` VALUES("4050", "en", "default", "273ac586f8c89c098004a170ce915f8b", "To reorganize menu items, <strong>drag and drop items with your mouse or use your keyboard</strong>. Drag or move a menu item a little to the right to make it a submenu", NULL, "LINE", NULL, "0", "", "dd26b4f2b1809abb4425114eedd4128c");
INSERT INTO `wp_icl_strings` VALUES("4051", "en", "default", "55ab0b440e5d2c1b104b85967683afb0", "Delete a menu item by <strong>expanding it and clicking the Remove link</strong>", NULL, "LINE", NULL, "0", "", "613616cc10a30e45173036e1bf1e1ff1");
