INSERT INTO `wp_icl_strings` VALUES("4116", "en", "woocommerce", "d3e3cf6d33a674b41429bb8cccf35b9a", "This controls the description which the user sees during checkout.", NULL, "LINE", NULL, "0", "", "8b2222bd631e0abd9e7df18a35cebcdd");
INSERT INTO `wp_icl_strings` VALUES("4117", "en", "woocommerce", "5bcb34ab6ac36b69cee34c8d6cd828d4", "Pay via PayPal; you can pay with your credit card if you don\'t have a PayPal account.", NULL, "LINE", NULL, "0", "", "42628cdbcfede47503b33a02c1e055a3");
