INSERT INTO `wp_icl_strings` VALUES("4128", "en", "woocommerce", "39b2928ad095538403f4b82ef5faf8a3", "If your main PayPal email differs from the PayPal email entered above, input your main receiver email for your PayPal account here. This is used to validate IPN requests.", NULL, "LINE", NULL, "0", "", "6e1c0c8c9abb03c29ec169efe8cbd2f2");
INSERT INTO `wp_icl_strings` VALUES("4129", "en", "woocommerce", "bb5b002dd85bfa7c6d5b5a79bbccabdd", "PayPal identity token", NULL, "LINE", NULL, "0", "", "9e4db4b4e8eaddad24c17280675b70f0");
INSERT INTO `wp_icl_strings` VALUES("4130", "en", "woocommerce", "b129f7c77d064f83b20f1a4f424799f2", "Optionally enable \"Payment Data Transfer\" (Profile > Profile and Settings > My Selling Tools > Website Preferences) and then copy your identity token here. This will allow payments to be verified without the need for PayPal IPN.", NULL, "LINE", NULL, "0", "", "a166e599dd4f71889932c25ad57996da");
