INSERT INTO `wp_icl_strings` VALUES("4259", "en", "woocommerce-gateway-paypal-express-checkout", "02dc63ed2318dd44331fa48e15678a4b", "Logo Image (190×60)", NULL, "LINE", NULL, "0", "", "2f914c716aa7857a56c1d54a11f6436f");
INSERT INTO `wp_icl_strings` VALUES("4260", "en", "woocommerce-gateway-paypal-express-checkout", "7e75a0376ee4b0220278c2a9dee4961c", "If you want PayPal to co-brand the checkout page with your logo, enter the URL of your logo image here.<br/>The image must be no larger than 190x60, GIF, PNG, or JPG format, and should be served over HTTPS.", NULL, "LINE", NULL, "0", "", "cadee829e9ce603bca19aac00c06990c");
INSERT INTO `wp_icl_strings` VALUES("4261", "en", "woocommerce-gateway-paypal-express-checkout", "87b57e5d713ce8a0ebddfcdb5c23373c", "Header Image (750×90)", NULL, "LINE", NULL, "0", "", "1e4a9296ca0d32a0c98959a5e43fc618");
INSERT INTO `wp_icl_strings` VALUES("4262", "en", "woocommerce-gateway-paypal-express-checkout", "35c17b9ceebfd7b6441135e1645c99d5", "If you want PayPal to co-brand the checkout page with your header, enter the URL of your header image here.<br/>The image must be no larger than 750x90, GIF, PNG, or JPG format, and should be served over HTTPS.", NULL, "LINE", NULL, "0", "", "e15c98fd64b6643c016605762fae2ce4");
