INSERT INTO `wp_icl_strings` VALUES("4269", "en", "woocommerce-gateway-paypal-express-checkout", "fff1528cd6492eba6879462c0d97ec36", "This enables PayPal Credit, which displays a PayPal Credit button next to the Express Checkout button. PayPal Express Checkout lets you give customers access to financing through PayPal Credit® - at no additional cost to you. You get paid up front, even though customers have more time to pay. A pre-integrated payment button shows up next to the PayPal Button, and lets customers pay quickly with PayPal Credit®.", NULL, "LINE", NULL, "0", "", "edcd5f6dbe710122f99d84b2d3d96c3f");
