INSERT INTO `wp_icl_strings` VALUES("4280", "en", "woocommerce-gateway-paypal-express-checkout", "c016aa56d5a9de168d70af4f05cdde7c", "PayPal only returns a shipping address back to the website. To make sure billing address is returned as well, please enable this functionality on your PayPal account by calling %1$sPayPal Technical Support%2$s.", NULL, "LINE", NULL, "0", "", "f43c26d0b7b30a8b3d7ddab1f34b0106");
