INSERT INTO `wp_icl_strings` VALUES("4288", "en", "woocommerce-gateway-paypal-express-checkout", "1f2de9452199aaf3420fa08765d1caa8", "Require Instant Payment", NULL, "LINE", NULL, "0", "", "2dacc9e99f45309b07deb6ca83d29f0c");
INSERT INTO `wp_icl_strings` VALUES("4289", "en", "woocommerce-gateway-paypal-express-checkout", "7838b5977a3364c1be49404327406654", "If you enable this setting, PayPal will be instructed not to allow the buyer to use funding sources that take additional time to complete (for example, eChecks). Instead, the buyer will be required to use an instant funding source, such as an instant transfer, a credit/debit card, or PayPal Credit.", NULL, "LINE", NULL, "0", "", "5ea9a61df3544124c8549ebe77d96de7");
INSERT INTO `wp_icl_strings` VALUES("4290", "en", "woocommerce-gateway-paypal-express-checkout", "6af579c3b6cf6dbe4e8be000653575f2", "Subtotal Mismatch Behavior", NULL, "LINE", NULL, "0", "", "398245cb8959e5da7d4ef7a42d4dbbef");
INSERT INTO `wp_icl_strings` VALUES("4291", "en", "woocommerce-gateway-paypal-express-checkout", "4ab219419dd45f32e506bec650b50580", "Internally, WC calculates line item prices and taxes out to four decimal places; however, PayPal can only handle amounts out to two decimal places (or, depending on the currency, no decimal places at all). Occasionally, this can cause discrepancies between the way WooCommerce calculates prices versus the way PayPal calculates them. If a mismatch occurs, this option controls how the order is dealt with so payment can still be taken.", NULL, "LINE", NULL, "0", "", "916d85d16710d0c838b5c3ea94bef864");
