INSERT INTO `wp_icl_strings` VALUES("4607", "en", "default", "9d51d9ae2be6a514b532a630672a22fd", "Add Plugins", NULL, "LINE", NULL, "0", "", "a5f0be294fbf81f629a0f2dadbb5899c");
INSERT INTO `wp_icl_strings` VALUES("4608", "en", "default", "6ce32e02fc5aad094d1708046e3ddc76", "Plugins hook into WordPress to extend its functionality with custom features. Plugins are developed independently from the core WordPress application by thousands of developers all over the world. All plugins in the official <a href=\"%s\">WordPress Plugin Directory</a> are compatible with the license WordPress uses.", NULL, "LINE", NULL, "0", "", "9f0c80252eb8b23b4d92b0ac43489e45");
INSERT INTO `wp_icl_strings` VALUES("4609", "en", "default", "2a4f1cc2e99d292a9132ee9cfb9c3b26", "You can find new plugins to install by searching or browsing the directory right here in your own Plugins section.", NULL, "LINE", NULL, "0", "", "f21a09c0e4a3b3eee841ef6b410151fa");
INSERT INTO `wp_icl_strings` VALUES("4610", "en", "default", "9bb3c737be1800296ce0387599667655", "Adding Plugins", NULL, "LINE", NULL, "0", "", "f55ea180d89fafcea5f6224169b61ffb");
INSERT INTO `wp_icl_strings` VALUES("4611", "en", "default", "a379ef32870b64a6ef175d6e3da0bf17", "If you know what you&#8217;re looking for, Search is your best bet. The Search screen has options to search the WordPress Plugin Directory for a particular Term, Author, or Tag. You can also search the directory by selecting popular tags. Tags in larger type mean more plugins have been labeled with that tag.", NULL, "LINE", NULL, "0", "", "2dfd3ebb5bbf8256c5c0cc8fefb8b3c8");
INSERT INTO `wp_icl_strings` VALUES("4612", "en", "default", "2448f34ad2e38271ffbcf4d299c17c5d", "If you just want to get an idea of what&#8217;s available, you can browse Featured and Popular plugins by using the links above the plugins list. These sections rotate regularly.", NULL, "LINE", NULL, "0", "", "575aca988849707fdbbfd60be15902e1");
INSERT INTO `wp_icl_strings` VALUES("4613", "en", "default", "b816582da0fe96d33555b82e7cf35504", "You can also browse a user&#8217;s favorite plugins, by using the Favorites link above the plugins list and entering their WordPress.org username.", NULL, "LINE", NULL, "0", "", "96e13d80dd4d210f6e818be399382890");
INSERT INTO `wp_icl_strings` VALUES("4614", "en", "default", "be3d2742b2dbbb1878b22b8702b6e8d7", "If you want to install a plugin that you&#8217;ve downloaded elsewhere, click the Upload Plugin button above the plugins list. You will be prompted to upload the .zip package, and once uploaded, you can activate the new plugin.", NULL, "LINE", NULL, "0", "", "313943fe8cc1a3d691aa966a6a6819c7");
INSERT INTO `wp_icl_strings` VALUES("4615", "en", "default", "5222c43ea98bf538434547945c1d0249", "<a href=\"https://codex.wordpress.org/Plugins_Add_New_Screen\">Documentation on Installing Plugins</a>", NULL, "LINE", NULL, "0", "", "3506bc96a38a6c3fe95ae5091f4c3ecf");
INSERT INTO `wp_icl_strings` VALUES("4616", "en", "default", "3eb754f2c75e6f8174fe8544f1269e8c", "Browse Plugins", NULL, "LINE", NULL, "0", "", "c943f207ce2327b42eae6aae0fd7216a");
INSERT INTO `wp_icl_strings` VALUES("4617", "en", "default", "3e0109b6197e41063cfac796836f2dad", "If you have a plugin in a .zip format, you may install it by uploading it here.", NULL, "LINE", NULL, "0", "", "e095159d4751cb7d3f7fb57962f8b003");
