INSERT INTO `wp_icl_strings` VALUES("4624", "en", "default", "85b65109399a5c8314c6c0e4cb50cca8", "Plugins extend and expand the functionality of WordPress. You may automatically install plugins from the <a href=\"%1$s\">WordPress Plugin Directory</a> or upload a plugin in .zip format by clicking the button at the top of this page.", NULL, "LINE", NULL, "0", "", "c2d61fd293462329748a14cd39b728f3");
