INSERT INTO `wp_icl_strings` VALUES("4854", "en", "woocommerce", "520e33ea10e9c1562e848cd3886be710", "Should you need help understanding, using, or extending WooCommerce, <a href=\"%s\">please read our documentation</a>. You will find all kinds of resources including snippets, tutorials and much more.", NULL, "LINE", NULL, "0", "", "1d42fdd6cf1c7830d713cf002c6e0581");
INSERT INTO `wp_icl_strings` VALUES("4855", "en", "woocommerce", "209bf91fc619a085e461b4d2119b6367", "For further assistance with WooCommerce core you can use the <a href=\"%1$s\">community forum</a>. If you need help with premium extensions sold by WooCommerce, please <a href=\"%2$s\">use our helpdesk</a>.", NULL, "LINE", NULL, "0", "", "ede2726b4a982712a4e3a3b0fa119395");
INSERT INTO `wp_icl_strings` VALUES("4856", "en", "woocommerce", "fae190768e0c1452fe7e495f58ef1f51", "Before asking for help we recommend checking the system status page to identify any problems with your configuration.", NULL, "LINE", NULL, "0", "", "e80cc7eb99b8056b0a6c0bb8a56c099b");
