INSERT INTO `wp_icl_strings` VALUES("4859", "en", "woocommerce", "35b2f8dd3f3fa7036eedce0f55051e5c", "WooCommerce helpdesk", NULL, "LINE", NULL, "0", "", "6b02dbdc251e5df578d8d25228181336");
INSERT INTO `wp_icl_strings` VALUES("4860", "en", "woocommerce", "dadd416e98b2f9d7c99aaf2b50d3cb5a", "Found a bug?", NULL, "LINE", NULL, "0", "", "ec3e29fbc09905dee8767ce056e9f0c1");
INSERT INTO `wp_icl_strings` VALUES("4861", "en", "woocommerce", "d2a8fd36088b5ffeda8ee8ed664e9b7d", "If you find a bug within WooCommerce core you can create a ticket via <a href=\"%1$s\">Github issues</a>. Ensure you read the <a href=\"%2$s\">contribution guide</a> prior to submitting your report. To help us solve your issue, please be as descriptive as possible and include your <a href=\"%3$s\">system status report</a>.", NULL, "LINE", NULL, "0", "", "bbe24fbc4cf5dd6c4505bb48cd593569");
INSERT INTO `wp_icl_strings` VALUES("4862", "en", "woocommerce", "3406147c3ce729ce17ed76d45c2896f9", "Report a bug", NULL, "LINE", NULL, "0", "", "d728ae84c3c43e07e831dc14a2b36242");
INSERT INTO `wp_icl_strings` VALUES("4863", "en", "woocommerce", "de7a22a0c94aa64ba2449e520aa20c99", "Education", NULL, "LINE", NULL, "0", "", "6c74872728f10b7750523281c9a006b0");
