INSERT INTO `wp_icl_strings` VALUES("6889", "en", "gravityforms", "5176c3b9bc67075252308206accd4eb1", "Select the format you would like to use for the Name field.  There are 3 options, Normal which includes First and Last Name, Extended which adds Prefix and Suffix, or Simple which is a single input field.", NULL, "LINE", NULL, "0", "", "2837bbc60ee622acbae2425846cceaf9");
INSERT INTO `wp_icl_strings` VALUES("6890", "en", "gravityforms", "fb660cd169b3f07a643b0553d900505f", "Number Format", NULL, "LINE", NULL, "0", "", "73d30ca40e29a1d1cc532161a143c70d");
INSERT INTO `wp_icl_strings` VALUES("6891", "en", "gravityforms", "0125ad8cb042f83ea4e63f1884781fb8", "Select the format of numbers that are allowed in this field. You have the option to use a comma or a dot as the decimal separator.", NULL, "LINE", NULL, "0", "", "ad8ea0db7b1f112e668291b8b78237e5");
INSERT INTO `wp_icl_strings` VALUES("6892", "en", "gravityforms", "1095c689ab50a6d7584cd5b44af783b1", "Force SSL", NULL, "LINE", NULL, "0", "", "b41a77f017644284f9fbdb5ff90e7d66");
INSERT INTO `wp_icl_strings` VALUES("6893", "en", "gravityforms", "d45cf5719361550b9acc3907f0037673", "Check this box to prevent this field from being displayed in a non-secure page (i.e. not https://). It will redirect the page to the same URL, but starting with https:// instead. This option requires a properly configured SSL certificate.", NULL, "LINE", NULL, "0", "", "448d40cb892aef5a0c513a0998c18e36");
INSERT INTO `wp_icl_strings` VALUES("6894", "en", "gravityforms", "589f30ddac8e23e268cad2f6b7185b0a", "Credit Card Icon Style", NULL, "LINE", NULL, "0", "", "238d4bba22086a5a443ce868a00e9477");
INSERT INTO `wp_icl_strings` VALUES("6895", "en", "gravityforms", "e8069af1a3935805c0e667d4dc8ff407", "Select the style you would like to use for the credit card icons.", NULL, "LINE", NULL, "0", "", "070681cc517377cc41c128527e88481c");
INSERT INTO `wp_icl_strings` VALUES("6896", "en", "gravityforms", "9ca12e1e01e649763e4c355c0b18b020", "Field Date Format", NULL, "LINE", NULL, "0", "", "811431b3feac3b9587d0d3fe312e9a74");
INSERT INTO `wp_icl_strings` VALUES("6897", "en", "gravityforms", "7b993c48a53695ed02f98c6fcd56f896", "Select the format you would like to use for the date input.  Available options are MM/DD/YYYY and DD/MM/YYYY.", NULL, "LINE", NULL, "0", "", "7e7a78f2cfcb21154cb88a8dff26e6e9");
