INSERT INTO `wp_icl_strings` VALUES("6899", "en", "gravityforms", "27671009a843651b2ad372fbdcf04495", "Select the format you would like to use for the time field.  Available options are 12 hour (i.e. 8:30 pm) and 24 hour (i.e. 20:30).", NULL, "LINE", NULL, "0", "", "2bd7ae3f5ff6f49574cfe4a9df9387d6");
INSERT INTO `wp_icl_strings` VALUES("6900", "en", "gravityforms", "243a11b5ff5a5a6bccad9cd5b8e13ed1", "Allowed File Extensions", NULL, "LINE", NULL, "0", "", "b8d74c9a0d7afd00b27f0b348192135d");
INSERT INTO `wp_icl_strings` VALUES("6901", "en", "gravityforms", "58c5e0e6e9f4186c9196aab69d44b08e", "Enter the allowed file extensions for file uploads.  This will limit the type of files a user may upload.", NULL, "LINE", NULL, "0", "", "8507d3046d0bf20ae0b4d8624d06ce07");
INSERT INTO `wp_icl_strings` VALUES("6902", "en", "gravityforms", "126481008defb78beb3ead09dd0b40c1", "Enable Multi-File Upload", NULL, "LINE", NULL, "0", "", "c7054aee0285256c85960b73bedc0899");
INSERT INTO `wp_icl_strings` VALUES("6903", "en", "gravityforms", "3122c596aa8bbe61a80926dc34b43d2f", "Select this option to enable multiple files to be uploaded for this field.", NULL, "LINE", NULL, "0", "", "fe4f41537761c0c1b122b6dbbc16c780");
INSERT INTO `wp_icl_strings` VALUES("6904", "en", "gravityforms", "a2fad21ac8f90b3563c036c90f5adcf0", "Maximum Number of Files", NULL, "LINE", NULL, "0", "", "553cbb945724e96fabdd999c5664f46b");
INSERT INTO `wp_icl_strings` VALUES("6905", "en", "gravityforms", "ef262ec554f8e8eebe594067dcbca6d8", "Specify the maximum number of files that can be uploaded using this field. Leave blank for unlimited. Note that the actual number of files permitted may be limited by this server\'s specifications and configuration.", NULL, "LINE", NULL, "0", "", "cda5921ac96d2dc051d22160e7c0581e");
INSERT INTO `wp_icl_strings` VALUES("6906", "en", "gravityforms", "6dc8fa343d57994e13aa6c915a778f23", "Maximum File Size", NULL, "LINE", NULL, "0", "", "4b27af1d4779ddade82db49277c8b89e");
INSERT INTO `wp_icl_strings` VALUES("6907", "en", "gravityforms", "d8731768f94fe24b8dccbb05b9ee702f", "Specify the maximum file size in megabytes allowed for each of the files.", NULL, "LINE", NULL, "0", "", "526ace9886e68475728976c1a5b08d88");
INSERT INTO `wp_icl_strings` VALUES("6908", "en", "gravityforms", "932d17f83495bf02fbbeba6733131d95", "Phone Number Format", NULL, "LINE", NULL, "0", "", "4860a0dc93660ab322a2bd658ccbeb34");
INSERT INTO `wp_icl_strings` VALUES("6909", "en", "gravityforms", "5219a83b78948a5799fef4d93bbbdb7a", "Select the format you would like to use for the phone input.  Available options are domestic US/CANADA style phone number and international long format phone number.", NULL, "LINE", NULL, "0", "", "1bbd80dfae84f48548a954540add222c");
INSERT INTO `wp_icl_strings` VALUES("6930", "en", "gravityforms", "efec06f8ce9fd050d0d34386cbeb8683", "Field Description", NULL, "LINE", NULL, "0", "", "ec0b4662a0c3691f903ee2f7ec912d0f");
INSERT INTO `wp_icl_strings` VALUES("6931", "en", "gravityforms", "a4f1c0619281abbe59a8a371ebaa63cb", "Enter the description for the form field.  This will be displayed to the user and provide some direction on how the field should be filled out or selected.", NULL, "LINE", NULL, "0", "", "0485e77cfd7b36e4679536b206120521");
INSERT INTO `wp_icl_strings` VALUES("6932", "en", "gravityforms", "09737938747269600702609ab170f2f5", "Required Field", NULL, "LINE", NULL, "0", "", "cecb8bebc34c9537e58fc3c206234d9e");
INSERT INTO `wp_icl_strings` VALUES("6933", "en", "gravityforms", "21510c674e1083a837d4624888758e29", "Select this option to make the form field required.  A required field will prevent the form from being submitted if it is not filled out or selected.", NULL, "LINE", NULL, "0", "", "093492b3cb8209cc0cfc37041d451709");
INSERT INTO `wp_icl_strings` VALUES("6934", "en", "gravityforms", "7f2821fb27b2458a4875360790bb6fa9", "No Duplicates", NULL, "LINE", NULL, "0", "", "fd57a4b5f8a7a2808c896bac643aa1e5");
INSERT INTO `wp_icl_strings` VALUES("6935", "en", "gravityforms", "fdf9f67567734ab487b726e8c0eaad57", "Select this option to limit user input to unique values only.  This will require that a value entered in a field does not currently exist in the entry database for that field.", NULL, "LINE", NULL, "0", "", "a7ba74fc8c36a2233c88dfb7f77b9726");
INSERT INTO `wp_icl_strings` VALUES("6936", "en", "gravityforms", "3b6d2398020a0fe3b6bce8e7a37ac512", "Number Range", NULL, "LINE", NULL, "0", "", "4df22f3cdb533507593a034eb64b9b0a");
