INSERT INTO `wp_icl_strings` VALUES("6938", "en", "gravityforms", "6e8ef6b7b19b37beb419da0c4c814386", "Enable Calculation", NULL, "LINE", NULL, "0", "", "5307f4da6fbb7b40c7cbd57ffb8e12e7");
