INSERT INTO `wp_icl_strings` VALUES("6940", "en", "gravityforms", "313a6a3d25aa041ee3dc3cbd65d4f22b", "Formula", NULL, "LINE", NULL, "0", "", "25604560e031b0273c7878038e5a6617");
INSERT INTO `wp_icl_strings` VALUES("6941", "en", "gravityforms", "304a280cb95d8f293c0f90fc1760683d", "Specify a mathematical formula. The result of this formula will be dynamically populated as the value for this field.", NULL, "LINE", NULL, "0", "", "e7d30ef0ead59e2e797cf84e2ff5af36");
INSERT INTO `wp_icl_strings` VALUES("6942", "en", "gravityforms", "f81873c32fc169ee4afa8ea831f6aba4", "Rounding", NULL, "LINE", NULL, "0", "", "f79e3fd31c596178fc40d3a332be92a4");
