INSERT INTO `wp_icl_strings` VALUES("6944", "en", "gravityforms", "42572c680e1750331ca5dbb3a80a4477", "Admin Label", NULL, "LINE", NULL, "0", "", "8b6b0e7dbe2ce0e08ec5b2344465fee3");
