INSERT INTO `wp_icl_strings` VALUES("6947", "en", "gravityforms", "0fc38e3d99e9c8aae3247e29964ede89", "Enter values in this setting to override the Sub-Label for each field.", NULL, "LINE", NULL, "0", "", "1aa7b90162fa360110dd53b562e64ada");
