INSERT INTO `wp_icl_strings` VALUES("6950", "en", "gravityforms", "69ade869db85db2c11c64a7abc91e2a4", "Select the description placement.  Descriptions can be placed above the field inputs or below the field inputs.", NULL, "LINE", NULL, "0", "", "7f5fe4f781f41a9f7b22faa1d28ba6c5");
INSERT INTO `wp_icl_strings` VALUES("6951", "en", "gravityforms", "afadb280d32925db45392768a706f02f", "Select the sub-label placement.  Sub-labels can be placed above the field inputs or below the field inputs.", NULL, "LINE", NULL, "0", "", "bb2d77bd24fdcf24aa243578b5f0d3d6");
