INSERT INTO `wp_icl_strings` VALUES("6956", "en", "gravityforms", "f42691fbfe15473918da4ccc20af7e69", "Name Prefix Choices", NULL, "LINE", NULL, "0", "", "f572f2144912e5b80389d43cea834e64");
