INSERT INTO `wp_icl_strings` VALUES("6959", "en", "gravityforms", "ef05259445356bdfb42297ef3436c67a", "Select the fields you\'d like to use in this Address Field and customize the Sub-Labels by entering new ones.", NULL, "LINE", NULL, "0", "", "582c8d3d6521ef438239cd9a7c9ea7e3");
