INSERT INTO `wp_icl_strings` VALUES("6972", "en", "gravityforms", "b61ac455ca2e9ba1fb9b523d7e42452a", "Source Field", NULL, "LINE", NULL, "0", "", "d3a1dab0d51740a90ff5a9e3ac1b81e6");
