INSERT INTO `wp_icl_strings` VALUES("6982", "en", "gravityforms", "1729a56cfc89021478498fe0c89a843a", "Visibility", NULL, "LINE", NULL, "0", "", "e8a6e721c33debdd3134f5e7f8fcf356");
INSERT INTO `wp_icl_strings` VALUES("6983", "en", "gravityforms", "76df0a2b2951def33430cfea5d85b0d9", "Select the visibility for this field. Field visibility set to Everyone will be visible by the user submitting the form. Form field visibility set to Admin Only will only be visible within the Gravity Forms administration tool.<br /><br />Setting a field to Admin Only is useful for creating fields that can be used to set a status or priority level on submitted entries.", NULL, "LINE", NULL, "0", "", "1cf467144368c9f5123ba081a7b32d93");
INSERT INTO `wp_icl_strings` VALUES("6984", "en", "gravityforms", "340508c6c2e14cfced1a80a9a7a01925", "Field Choices", NULL, "LINE", NULL, "0", "", "f64296998e652e88081c807f01350ea2");
