INSERT INTO `wp_icl_strings` VALUES("7493", "en", "default", "05919284773edd49431bb2c25283c500", "Reading Settings", NULL, "LINE", NULL, "0", "", "6399142979b01a4eb76d14dfeb80c498");
INSERT INTO `wp_icl_strings` VALUES("7494", "en", "default", "33da0d47041802e3fd26e5365065010a", "This screen contains the settings that affect the display of your content.", NULL, "LINE", NULL, "0", "", "0d6aff48207c056f0e11a40873fd9a8b");
INSERT INTO `wp_icl_strings` VALUES("7495", "en", "default", "64569debf6bd3abbd212384c1a3d0a46", "You can choose what&#8217;s displayed on the front page of your site. It can be posts in reverse chronological order (classic blog), or a fixed/static page. To set a static home page, you first need to create two <a href=\"%s\">Pages</a>. One will become the front page, and the other will be where your posts are displayed.", NULL, "LINE", NULL, "0", "", "467557c5c3be0b00bf0e489063fc1179");
INSERT INTO `wp_icl_strings` VALUES("7496", "en", "default", "6b872e2b359ab19726c69cfc6662980b", "You can also control the display of your content in RSS feeds, including the maximum number of posts to display and whether to show full text or a summary.", NULL, "LINE", NULL, "0", "", "ac7ebf12f6abef2a950abe5280a54102");
INSERT INTO `wp_icl_strings` VALUES("7497", "en", "default", "84760e95a02e1b21fd067dcf8d357fdc", "You must click the Save Changes button at the bottom of the screen for new settings to take effect.", NULL, "LINE", NULL, "0", "", "40de51e1c0d646fdf0d7d9f4efaa3ea7");
INSERT INTO `wp_icl_strings` VALUES("7498", "en", "default", "f5752ab5c7a0fb0f40be53417ace7247", "Search Engine Visibility", NULL, "LINE", NULL, "0", "", "7cb2843c487aedb2909244818df8fdf8");
INSERT INTO `wp_icl_strings` VALUES("7499", "en", "default", "0754b49868a7a0ac641085882e136d75", "You can choose whether or not your site will be crawled by robots, ping services, and spiders. If you want those services to ignore your site, click the checkbox next to &#8220;Discourage search engines from indexing this site&#8221; and click the Save Changes button at the bottom of the screen. Note that your privacy is not complete; your site is still visible on the web.", NULL, "LINE", NULL, "0", "", "1051f547d613d166e58ee2267ea6046e");
INSERT INTO `wp_icl_strings` VALUES("7500", "en", "default", "4210a86ca0a86ebdf5712c60c47a01ca", "When this setting is in effect, a reminder is shown in the At a Glance box of the Dashboard that says, &#8220;Search Engines Discouraged,&#8221; to remind you that your site is not being crawled.", NULL, "LINE", NULL, "0", "", "33960768ca0efb2109671f1ab02318f6");
INSERT INTO `wp_icl_strings` VALUES("7501", "en", "default", "683f4106f8e5ad2034b19900421a4f8e", "<a href=\"https://codex.wordpress.org/Settings_Reading_Screen\">Documentation on Reading Settings</a>", NULL, "LINE", NULL, "0", "", "8bcd56596f7cc9e41ad8785e74eac871");
