INSERT INTO `wp_icl_strings` VALUES("7836", "en", "default", "db3af42ba64b595db6ab3ea999fe0bb1", "Drafts", NULL, "LINE", NULL, "0", "", "430564c7cd269a745a1077be966cb4e4");
INSERT INTO `wp_icl_strings` VALUES("7837", "en", "default", "19b8a23b0794f1d1b2353f149aa2a575", "WordPress Events and News", NULL, "LINE", NULL, "0", "", "4449065eccd9c6d7a467f72174a20ed6");
INSERT INTO `wp_icl_strings` VALUES("7838", "en", "default", "7bb5b05b47216f59eeab4b23d0014277", "Welcome to your WordPress Dashboard! This is the screen you will see when you log in to your site, and gives you access to all the site management features of WordPress. You can get help for any screen by clicking the Help tab above the screen title.", NULL, "LINE", NULL, "0", "", "caa3ada644015d7da497c61e78d64e63");
INSERT INTO `wp_icl_strings` VALUES("7839", "en", "default", "030eb735ca4d9aed22505e6adb6e37c1", "The left-hand navigation menu provides links to all of the WordPress administration screens, with submenu items displayed on hover. You can minimize this menu to a narrow icon strip by clicking on the Collapse Menu arrow at the bottom.", NULL, "LINE", NULL, "0", "", "a199554e617c102a6467d92c120ed216");
INSERT INTO `wp_icl_strings` VALUES("7840", "en", "default", "ca887a8513c595e757361973c401dfc3", "Links in the Toolbar at the top of the screen connect your dashboard and the front end of your site, and provide access to your profile and helpful WordPress information.", NULL, "LINE", NULL, "0", "", "9ae02a33eb223e22333569fc7a0b76e7");
INSERT INTO `wp_icl_strings` VALUES("7841", "en", "default", "846495f9ceed11accf8879f555936a7d", "Navigation", NULL, "LINE", NULL, "0", "", "52b4c7dc9c72494863e18b45a3f4c4ce");
INSERT INTO `wp_icl_strings` VALUES("7842", "en", "default", "5d3cfb1969cd32ce072f4a154329dbf9", "You can use the following controls to arrange your Dashboard screen to suit your workflow. This is true on most other administration screens as well.", NULL, "LINE", NULL, "0", "", "96659bd2478b2ec608973ec63c74de4c");
INSERT INTO `wp_icl_strings` VALUES("7843", "en", "default", "a98be58ec342822c43f9a60f423b1d2f", "<strong>Screen Options</strong> &mdash; Use the Screen Options tab to choose which Dashboard boxes to show.", NULL, "LINE", NULL, "0", "", "ed407f0af001c6226b4cc9be2875d701");
INSERT INTO `wp_icl_strings` VALUES("7844", "en", "default", "71ce1121b4c5579d8f2c8e71335b9a22", "<strong>Drag and Drop</strong> &mdash; To rearrange the boxes, drag and drop by clicking on the title bar of the selected box and releasing when you see a gray dotted-line rectangle appear in the location you want to place the box.", NULL, "LINE", NULL, "0", "", "5152a8dbc58f8c3531ec3b414be417aa");
INSERT INTO `wp_icl_strings` VALUES("7845", "en", "default", "b65fa8d75f92ea10c226c9c9e107cfc6", "<strong>Box Controls</strong> &mdash; Click the title bar of the box to expand or collapse it. Some boxes added by plugins may have configurable content, and will show a &#8220;Configure&#8221; link in the title bar if you hover over it.", NULL, "LINE", NULL, "0", "", "54545fe38eda999a7a67927af2e0e559");
INSERT INTO `wp_icl_strings` VALUES("7846", "en", "default", "7ab07ba8c965ea3d313a005f2b1be7e1", "The boxes on your Dashboard screen are:", NULL, "LINE", NULL, "0", "", "0c233d949e877e2af23f5c041e05bb6b");
INSERT INTO `wp_icl_strings` VALUES("7847", "en", "default", "a8f4d14621490c3a1b94015a6744f684", "<strong>At A Glance</strong> &mdash; Displays a summary of the content on your site and identifies which theme and version of WordPress you are using.", NULL, "LINE", NULL, "0", "", "63cb0e79249a3955ef8698cb0ff6b575");
INSERT INTO `wp_icl_strings` VALUES("7848", "en", "default", "ecdb52fb00d5aabe4e9d24b436f9b9fc", "<strong>Activity</strong> &mdash; Shows the upcoming scheduled posts, recently published posts, and the most recent comments on your posts and allows you to moderate them.", NULL, "LINE", NULL, "0", "", "92492a427dfb72cd1b97719e8e35e896");
INSERT INTO `wp_icl_strings` VALUES("7849", "en", "default", "78bf51b73e62500109ff97739c24bb7d", "<strong>Quick Draft</strong> &mdash; Allows you to create a new post and save it as a draft. Also displays links to the 5 most recent draft posts you\'ve started.", NULL, "LINE", NULL, "0", "", "ccbc678b1234e3865ff243cf2f5e033b");
INSERT INTO `wp_icl_strings` VALUES("7850", "en", "default", "68be54ef7492257e7178f816454fdf5a", "<strong>WordPress News</strong> &mdash; Latest news from the official WordPress project, the <a href=\"%s\">WordPress Planet</a>, and popular plugins.", NULL, "LINE", NULL, "0", "", "9c5423e69e0284c407f9aabcee4dd836");
