INSERT INTO `wp_icl_strings` VALUES("8005", "en", "sitepress", "8cf9066ae77e9e97511c324d9c18a4b4", "This operation copies the content from the original language onto this translation. It\'s meant for when you want to start with the original content, but keep translating in this language. This button is only enabled when there\'s no content in the editor.", NULL, "LINE", NULL, "0", "", "da1064f6c5c591eb72aa5371299ddf45");
