INSERT INTO `wp_icl_strings` VALUES("8512", "en", "default", "2042039084309ff51e79ad865fddfe18", "Permalink Settings", NULL, "LINE", NULL, "0", "", "c76844e39b33480bb6fe2e4cf46768b8");
INSERT INTO `wp_icl_strings` VALUES("8513", "en", "default", "cf58d0bdb1a7290be283d6eecf7ae494", "Permalinks are the permanent URLs to your individual pages and blog posts, as well as your category and tag archives. A permalink is the web address used to link to your content. The URL to each post should be permanent, and never change &#8212; hence the name permalink.", NULL, "LINE", NULL, "0", "", "5e17346941e1c3c5429df2cd864628f8");
INSERT INTO `wp_icl_strings` VALUES("8514", "en", "default", "30f9f179137df7ca8ffbfb5f2f1e3c07", "This screen allows you to choose your permalink structure. You can choose from common settings or create custom URL structures.", NULL, "LINE", NULL, "0", "", "a24d06ba20bbc9ad1b7f8b3d334e7d15");
INSERT INTO `wp_icl_strings` VALUES("8515", "en", "default", "80eb21ed1fac7886258fd1da7c3268bc", "Permalinks can contain useful information, such as the post date, title, or other elements. You can choose from any of the suggested permalink formats, or you can craft your own if you select Custom Structure.", NULL, "LINE", NULL, "0", "", "341dbad1916c2decd6aead18f094b18b");
INSERT INTO `wp_icl_strings` VALUES("8516", "en", "default", "1a89b9744e617ef3fb4ab7c4fffee90c", "If you pick an option other than Plain, your general URL path with structure tags (terms surrounded by <code>%</code>) will also appear in the custom structure field and your path can be further modified there.", NULL, "LINE", NULL, "0", "", "8a085eced0d0591c402c82cff68cbc18");
INSERT INTO `wp_icl_strings` VALUES("8517", "en", "default", "cd8d1a19831be7da0547b6a5fcf33156", "When you assign multiple categories or tags to a post, only one can show up in the permalink: the lowest numbered category. This applies if your custom structure includes <code>%category%</code> or <code>%tag%</code>.", NULL, "LINE", NULL, "0", "", "898670c2e52a88ad0454c93711770605");
INSERT INTO `wp_icl_strings` VALUES("8518", "en", "default", "313a4258db970729195e2e53fe33412e", "Custom Structures", NULL, "LINE", NULL, "0", "", "6df19e5a9afce680bb53549314ac67cd");
INSERT INTO `wp_icl_strings` VALUES("8519", "en", "default", "e3a6c830a45973f8caac3432d436e903", "The Optional fields let you customize the &#8220;category&#8221; and &#8220;tag&#8221; base names that will appear in archive URLs. For example, the page listing all posts in the &#8220;Uncategorized&#8221; category could be <code>/topics/uncategorized</code> instead of <code>/category/uncategorized</code>.", NULL, "LINE", NULL, "0", "", "c686ed3a11e088be1973308ebe9e40cb");
INSERT INTO `wp_icl_strings` VALUES("8520", "en", "default", "a451bd8bf8bba202e1d9ea3711855f7e", "<a href=\"https://codex.wordpress.org/Settings_Permalinks_Screen\">Documentation on Permalinks Settings</a>", NULL, "LINE", NULL, "0", "", "7c7865bf718adb0ed9a3abd86a6efd47");
INSERT INTO `wp_icl_strings` VALUES("8521", "en", "default", "32422622cd895670750d6f8e3e878c4c", "<a href=\"https://codex.wordpress.org/Using_Permalinks\">Documentation on Using Permalinks</a>", NULL, "LINE", NULL, "0", "", "72ee400c9062c148c288dab26d78ae6d");
INSERT INTO `wp_icl_strings` VALUES("8522", "en", "default", "f2187a07f70a08b8d7c5fdca5f133a0e", "WordPress offers you the ability to create a custom URL structure for your permalinks and archives. Custom URL structures can improve the aesthetics, usability, and forward-compatibility of your links. A <a href=\"https://codex.wordpress.org/Using_Permalinks\">number of tags are available</a>, and here are some examples to get you started.", NULL, "LINE", NULL, "0", "", "ef56828a2feacb6d1071e6f9c4e5adf5");
INSERT INTO `wp_icl_strings` VALUES("8523", "en", "default", "1a557283e5542468721ccbc770c8b13f", "archives", NULL, "LINE", NULL, "0", "sample permalink base", "893deb279edb537cdde635951eeeea6e");
