INSERT INTO `wp_icl_strings` VALUES("8533", "en", "default", "864e67dda7ff0dc96db5c841bdbf98ad", "If you like, you may enter custom structures for your category and tag URLs here. For example, using <code>topics</code> as your category base would make your category links like <code>%s/topics/uncategorized/</code>. If you leave these blank the defaults will be used.", NULL, "LINE", NULL, "0", "", "d635a782b4c7393661373e724751b36c");
