INSERT INTO `wp_icl_strings` VALUES("9751", "en", "duplicator", "73a1a5b7627e101bd71a1621bd125809", "File filters allow you to ignore directories and file extensions.  When creating a package only include the data you want and need.  This helps to improve the overall archive build time and keep your backups simple and clean.", NULL, "LINE", NULL, "0", "", "3baf9e535295c707bd56d09eb2829a9b");
