INSERT INTO `wp_icl_strings` VALUES("9764", "en", "duplicator", "d913d87f02e4b5bfc5a326fa2d8a2b0c", "<b>Overview:</b><br/> This advanced option excludes all files from the archive.  Only the database and a copy of the installer.php will be included in the archive.zip file. The option can be used for backing up and moving only the database. <i>Please note that this option is currently in *Beta*.</i>", NULL, "LINE", NULL, "0", "", "5a446d03eebbce8ecc490d2ccb383e9f");
INSERT INTO `wp_icl_strings` VALUES("9765", "en", "duplicator", "c47a32628ca02e94e7aa824fd6270978", "<b><i class=\'fa fa-exclamation-circle\'></i> Notice:</b><br/>  Installing only the database over an existing site may have unintended consequences.  Be sure to know the state of your system before installing the database without the associated files. ", NULL, "LINE", NULL, "0", "", "a45fa253bba43ac26815cb4afeac7cfc");
INSERT INTO `wp_icl_strings` VALUES("9766", "en", "duplicator", "4c1c1ed78dc4125ebdf532496e21f06e", "For example, if you have WordPress 4.6 on this site and you copy this sites database to a host that has WordPress 4.8 files then the source code of the files  will not be in sync with the database causing possible errors.", NULL, "LINE", NULL, "0", "", "16944431a8db24d8e5d75a7bb1c208cc");
INSERT INTO `wp_icl_strings` VALUES("9767", "en", "duplicator", "0829ffc08f1a0adf030dbc811aceb123", "This can also be true of plugins and themes.   When moving only the database be sure to know the database will be compatible with ALL source code files.  Please use this advanced feature with caution!", NULL, "LINE", NULL, "0", "", "ac05272d55f42bd098c37e308c205a93");
