INSERT INTO `wp_icl_strings` VALUES("9776", "en", "duplicator", "c3e82e15e7850c4166e3327453ebf083", "This is an advanced database backwards compatibility feature that should ONLY be used if having problems installing packages. If the database server version is lower than the version where the package was built then these options may help generate a script that is more compliant with the older database server. It is recommended to try each option separately starting with mysql40.", NULL, "LINE", NULL, "0", "", "65237be72504636bbb3c43a0b2089389");
