INSERT INTO `wp_icl_strings` VALUES("9822", "en", "duplicator", "97276f048fa51ea28ea3b9042355954a", "Supported web servers: ", NULL, "LINE", NULL, "0", "", "e3f268171c18b28edf43a691766635f3");
INSERT INTO `wp_icl_strings` VALUES("9823", "en", "duplicator", "ab64e3d5feb645333b320d80a84c8255", "PHP Version", NULL, "LINE", NULL, "0", "", "91efc7f526d139efafbd8ef7abf8a747");
INSERT INTO `wp_icl_strings` VALUES("9824", "en", "duplicator", "c68ea373919ebc25fae7f8edf7f5c2e7", "The minimum PHP version supported by Duplicator is 5.2.9. It is highly recommended to use PHP 5.3+ for improved stability.  For international language support please use PHP 7.0+.", NULL, "LINE", NULL, "0", "", "7d6757ac288a48acc0881b766e56f8e0");
INSERT INTO `wp_icl_strings` VALUES("9825", "en", "duplicator", "d60cf0d06de2f8db0890fa38b94de576", "PHP Open Base Dir", NULL, "LINE", NULL, "0", "", "90d9d1b15d48cd90f677912217cf95b5");
INSERT INTO `wp_icl_strings` VALUES("9826", "en", "duplicator", "e7fda7599bd4b38e499e35de424fb9d1", "Issues might occur when [open_basedir] is enabled. Work with your server admin to disable this value in the php.ini file if you’re having issues building a package.", NULL, "LINE", NULL, "0", "", "3a857252ba0067bee250a2b4fb3d3d6a");
INSERT INTO `wp_icl_strings` VALUES("9827", "en", "duplicator", "3dcc566e251244286981342828594de2", "PHP Max Execution Time", NULL, "LINE", NULL, "0", "", "dde2074d048c6af40a36014c60e089c6");
INSERT INTO `wp_icl_strings` VALUES("9828", "en", "duplicator", "ee06a7198306cf09bfcbedcfcf782758", "Timeouts may occur for larger packages when [max_execution_time] time in the php.ini is too low.  A value of 0 (recommended) indicates that PHP has no time limits. An attempt is made to override this value if the server allows it.", NULL, "LINE", NULL, "0", "", "553c3fa6a348645e0fa80c3c966f2ca9");
INSERT INTO `wp_icl_strings` VALUES("9829", "en", "duplicator", "59546632789fde5367eb3de4e40f6863", "Note: Timeouts can also be set at the web server layer, so if the PHP max timeout passes and you still see a build interrupt messages, then your web server could be killing the process.   If you are on a budget host and limited on processing time, consider using the database or file filters to shrink the size of your overall package.   However use caution as excluding the wrong resources can cause your install to not work properly.", NULL, "LINE", NULL, "0", "", "0a96a5efc97755b383ce07cec7bae934");
INSERT INTO `wp_icl_strings` VALUES("9830", "en", "duplicator", "fde31686817fe5029025586d51b35861", "WordPress", NULL, "LINE", NULL, "0", "", "e69795f73493bf135615ef2fa8eab1e2");
INSERT INTO `wp_icl_strings` VALUES("9831", "en", "duplicator", "b33681e5c8f412a380bd985420ae2309", "WordPress Version", NULL, "LINE", NULL, "0", "", "ac458218be6f8e8183722b69d11d00f8");
INSERT INTO `wp_icl_strings` VALUES("9832", "en", "duplicator", "235264b14aa07408c0a90e45442213f2", "It is recommended to have a version of WordPress that is greater than %1$s", NULL, "LINE", NULL, "0", "", "a5e636e08cfcaac390b98c6ce20773fc");
INSERT INTO `wp_icl_strings` VALUES("9833", "en", "duplicator", "b88c14c4d7db9c994f19ee70e0874944", "Core Files", NULL, "LINE", NULL, "0", "", "3d6a3c34571b162ba5f20a40de04c1d4");
INSERT INTO `wp_icl_strings` VALUES("9834", "en", "duplicator", "d99853c68fc517d00fb3f1e3158c9cf3", "If the scanner is unable to locate the wp-config.php file in the root directory, then you will need to manually copy it to its new location.", NULL, "LINE", NULL, "0", "", "c4438205556c682fde540657eac0c7f2");
INSERT INTO `wp_icl_strings` VALUES("9835", "en", "duplicator", "bffaa760ae92f5c766e01fa8655ce711", "Cache Path", NULL, "LINE", NULL, "0", "", "ca27fdee505c4744819bc881bc09e897");
INSERT INTO `wp_icl_strings` VALUES("9836", "en", "duplicator", "4b22795a795613bb3d137ed835e43ad9", "Cached data will lead to issues at install time and increases your archive size. Empty your cache directory before building the package by using  your cache plugins clear cache feature.  Use caution if manually removing files the cache folder. The cache size minimum threshold that triggers this warning is currently set at ", NULL, "LINE", NULL, "0", "", "1f54030e9a0b916d32c5c0192fc8fb51");
INSERT INTO `wp_icl_strings` VALUES("9837", "en", "duplicator", "0e08e4d58d185d38db50665e8f8661bf", "Multisite: N/A", NULL, "LINE", NULL, "0", "", "30291738f3c4850d37aa144e84b4a681");
INSERT INTO `wp_icl_strings` VALUES("9838", "en", "duplicator", "c04fe5c93ee6a6ae4c8241698eb2979f", "This is not a Multisite install so duplication will proceed without issue.  Duplicator does not officially support Multisite. However, Duplicator Pro supports duplication of a full Multisite network and also has the ability to install a Multisite subsite as a standalone site.", NULL, "LINE", NULL, "0", "", "70e12022197bd203b9d7e5d9e179cbb4");
INSERT INTO `wp_icl_strings` VALUES("9839", "en", "duplicator", "d8fbebae1b26efd50a3c40d7e3201865", "Show Scan Details", NULL, "LINE", NULL, "0", "", "09074dd04ce05a52282bf6f0b118eb15");
INSERT INTO `wp_icl_strings` VALUES("9840", "en", "duplicator", "4a9a5d68414f63d0ee34f958c06acf99", "Archive Size", NULL, "LINE", NULL, "0", "", "414c586fc68134c062ded2580f5648b0");
INSERT INTO `wp_icl_strings` VALUES("9841", "en", "duplicator", "ec3910e1f39c77c0e9cdc5c844ebcaef", "This size includes only files BEFORE compression is applied. It does not include the size of the database script or any applied filters.  Once complete the package size will be smaller than this number.", NULL, "LINE", NULL, "0", "", "557e1b3e8c6abf7597e9a4d6ed028e55");
INSERT INTO `wp_icl_strings` VALUES("9842", "en", "duplicator", "b10fd7ba043efce5a8d15d3534c3294d", "Size Checks", NULL, "LINE", NULL, "0", "", "29fd6eb58550b6d4fafa37df6caf4ed4");
INSERT INTO `wp_icl_strings` VALUES("9843", "en", "duplicator", "6f6cb72d544962fa333e2e34ce64f719", "Size", NULL, "LINE", NULL, "0", "", "09da62d43bb31077419f88f1b6b722e1");
INSERT INTO `wp_icl_strings` VALUES("9844", "en", "duplicator", "b05f0d41b637f86ee2555bfc971f76bf", "File Count", NULL, "LINE", NULL, "0", "", "3c654b8b0abd55a2b48162d4ae006837");
INSERT INTO `wp_icl_strings` VALUES("9845", "en", "duplicator", "502d8854d99523355596c42d03672fe4", "Directory Count", NULL, "LINE", NULL, "0", "", "4502d13765259aeb43e390fab146d10e");
INSERT INTO `wp_icl_strings` VALUES("9846", "en", "duplicator", "7cec9e8ff5e869d5e80c09b5fafe10e7", "Compressing larger sites on <i>some budget hosts</i> may cause timeouts.  ", NULL, "LINE", NULL, "0", "", "89cac98c03d6c31acb05a7a53114e4f9");
INSERT INTO `wp_icl_strings` VALUES("9847", "en", "duplicator", "cd87ff1e6a811df71960d18feeb01194", "more details...", NULL, "LINE", NULL, "0", "", "adfef95e3af7918b052f44ab4b4dc5bd");
INSERT INTO `wp_icl_strings` VALUES("9848", "en", "duplicator", "3b878279a04dc47d60932cb294d96259", "Overview", NULL, "LINE", NULL, "0", "", "8ea80fd244da8f5aab72f2c7dfc51b9b");
INSERT INTO `wp_icl_strings` VALUES("9849", "en", "duplicator", "e57492c40fba3440fbef11beed42a726", "This notice is triggered at <b>%s</b> and can be ignored on most hosts.  If during the build process you see a \"Host Build Interrupt\" message then this host has strict processing limits.  Below are some options you can take to overcome constraints setup on this host.", NULL, "LINE", NULL, "0", "", "8e148eb1fb245ba75cd3e0e9da2d8e5d");
INSERT INTO `wp_icl_strings` VALUES("9850", "en", "duplicator", "80f584637fbddd0d8a02d555535da071", "Timeout Options", NULL, "LINE", NULL, "0", "", "1d0e925259c5ec908171f546f66edff7");
INSERT INTO `wp_icl_strings` VALUES("9851", "en", "duplicator", "32fd34a15fe0f6c5f0a2c51f49e6c9b6", "Apply the \"Quick Filters\" below or click the back button to apply on previous page.", NULL, "LINE", NULL, "0", "", "8a9230b24244cb4733870c07057df202");
INSERT INTO `wp_icl_strings` VALUES("9852", "en", "duplicator", "4ba0fdae9529eb2382761a38bf4c527b", "See the FAQ link to adjust this hosts timeout limits: ", NULL, "LINE", NULL, "0", "", "305036b90cf97ae97d64c727ba9da034");
INSERT INTO `wp_icl_strings` VALUES("9853", "en", "duplicator", "e0b52e9711fe5035a10d4e23f1d115bc", "What can I try for Timeout Issues?", NULL, "LINE", NULL, "0", "", "2d923bc492bf042f37541afd9fb62486");
INSERT INTO `wp_icl_strings` VALUES("9854", "en", "duplicator", "0b4069bf857abdda5446a0cefe34cd85", "Consider trying multi-threaded support in ", NULL, "LINE", NULL, "0", "", "a45eb93275863176c2deff969cb4b620");
INSERT INTO `wp_icl_strings` VALUES("9855", "en", "duplicator", "9fd2a1a93067783428ee0cf0995861bf", "Duplicator Pro.", NULL, "LINE", NULL, "0", "", "949470c01a02b6f121ece51ebf3beda0");
INSERT INTO `wp_icl_strings` VALUES("9856", "en", "duplicator", "dbe27fcd9b85a49974a8f7b652c35904", "Files over %1$s are listed below. Larger files such as movies or zipped content can cause timeout issues on some budget hosts.  If you are having issues creating a package try excluding the directory paths below or go back to Step 1 and add them.", NULL, "LINE", NULL, "0", "", "c84362faa6190d3039b8f69f99e36584");
INSERT INTO `wp_icl_strings` VALUES("9857", "en", "duplicator", "8bac5ddb06972116f055f81c5856e1ff", "Quick Filters", NULL, "LINE", NULL, "0", "", "c755410625b3a16fef77b5b2249c33ac");
INSERT INTO `wp_icl_strings` VALUES("9858", "en", "duplicator", "168aeff0bb45eebba97cfc6db474e9b1", "Large Files", NULL, "LINE", NULL, "0", "", "42cf9f6130406315d221aa84c05f254a");
INSERT INTO `wp_icl_strings` VALUES("9859", "en", "duplicator", "92783c4df5c9852fe95ff19c054ddc56", "Hide All", NULL, "LINE", NULL, "0", "", "b1e03830765362994d9fd6f15b1401cd");
INSERT INTO `wp_icl_strings` VALUES("9860", "en", "duplicator", "8cd0d0c99b062b3d22e8c7188ba33ab2", "Show All", NULL, "LINE", NULL, "0", "", "3b014e2f972a3252db47d55c2b9f2254");
INSERT INTO `wp_icl_strings` VALUES("9861", "en", "duplicator", "b24b357476a82b529386c1dda0f3152a", "Root Path", NULL, "LINE", NULL, "0", "", "cd505ac2a81398e332dc096d93d462ad");
INSERT INTO `wp_icl_strings` VALUES("9862", "en", "duplicator", "093aceee41154502fe785bf34ae88916", "Core WordPress directories should not be filtered. Use caution when excluding files.", NULL, "LINE", NULL, "0", "", "b9364ea8aa194a858f92151f7fc68b1c");
INSERT INTO `wp_icl_strings` VALUES("9863", "en", "duplicator", "9cb93d7717cf3aff896a5113f470c2d9", "No large files found during this scan.", NULL, "LINE", NULL, "0", "", "18c05c7b94a3f8fbdae073b804d5b369");
INSERT INTO `wp_icl_strings` VALUES("9864", "en", "duplicator", "3208bd9e2867bf60127423569b44fc28", "Add Filters &amp; Rescan", NULL, "LINE", NULL, "0", "", "0e0723efd0ff2fbfdcc089ab6cd55e5f");
INSERT INTO `wp_icl_strings` VALUES("9865", "en", "duplicator", "21d146a945c3c69b5a16e9e272112629", "Copy Paths to Clipboard", NULL, "LINE", NULL, "0", "", "d00aa911c78c4e7a25528473b5520cce");
INSERT INTO `wp_icl_strings` VALUES("9866", "en", "duplicator", "9e5b49efa7d8c584f7fbe156f09a5b0f", "Name Checks", NULL, "LINE", NULL, "0", "", "1ca08d730b01d5bf238d67ae2d4e0b32");
INSERT INTO `wp_icl_strings` VALUES("9867", "en", "duplicator", "0184687c030c33529c4a327ec4544cc2", "Unicode and special characters such as \"*?><:/\\|\", can be problematic on some hosts.", NULL, "LINE", NULL, "0", "", "5d7b739718cb313b422338c1e1af7bf0");
INSERT INTO `wp_icl_strings` VALUES("9868", "en", "default", "2d5fef6c87f16217aa1b50e1ebc89720", "<b>", NULL, "LINE", NULL, "0", "", "a03443224fce2b5a0f139966192e231a");
INSERT INTO `wp_icl_strings` VALUES("9869", "en", "duplicator", "8fa2b18d72fde4bab1a9501e18d226b1", "  Only consider using this filter if the package build is failing. Select files that are not important to your site or you can migrate manually.", NULL, "LINE", NULL, "0", "", "46b113c1bfd40efe9248836a8b4df6ee");
INSERT INTO `wp_icl_strings` VALUES("9870", "en", "default", "5504ceea91e62031eccf9496eab44be7", "</b>", NULL, "LINE", NULL, "0", "", "59c4ef5fd9280bfe1814832966a3bcfb");
INSERT INTO `wp_icl_strings` VALUES("9871", "en", "duplicator", "fc1409e2968510309b12f3d9c4c7a347", "If this environment/system and the system where it will be installed are setup to support Unicode and long paths then these filters can be ignored.  If you run into issues with creating or installing a package, then is recommended to filter these paths.", NULL, "LINE", NULL, "0", "", "c34d4280050322d3e9b83ae52a3b121a");
INSERT INTO `wp_icl_strings` VALUES("9872", "en", "duplicator", "3c91bd4fa2955fcbf2e7f899447a3e45", "No file/directory name warnings found.", NULL, "LINE", NULL, "0", "", "5d679391189ef61316d2655d4c29a31b");
INSERT INTO `wp_icl_strings` VALUES("9873", "en", "duplicator", "66ccfcb3c5934afea080f061c85a2c91", "Database Size:", NULL, "LINE", NULL, "0", "", "6fb589b75cd28c3cb4e771499547a949");
INSERT INTO `wp_icl_strings` VALUES("9874", "en", "duplicator", "bffb8175a77e1c4ca38fd3de9a688c4f", "The database size represents only the included tables. The process for gathering the size uses the query SHOW TABLE STATUS.  The overall size of the database file can impact the final size of the package.", NULL, "LINE", NULL, "0", "", "0d8bf88f9b535b4f4d54f860a9102f41");
INSERT INTO `wp_icl_strings` VALUES("9875", "en", "duplicator", "c6e030a92f799137c9e91ca1d02724d8", "TOTAL SIZE", NULL, "LINE", NULL, "0", "", "8d8d8c5a0c757ef47eae3028c75f35f1");
INSERT INTO `wp_icl_strings` VALUES("9876", "en", "duplicator", "651ae5789ab776f2fcaf80b5e62801dc", "Tables", NULL, "LINE", NULL, "0", "", "db5bf059ef6f0284f4abfc669d252068");
INSERT INTO `wp_icl_strings` VALUES("9877", "en", "duplicator", "db231ec39f2ce4d5ad1de0abfc8970d9", "Records", NULL, "LINE", NULL, "0", "", "4dea82ebc229fbba95ba345e008ac603");
INSERT INTO `wp_icl_strings` VALUES("9878", "en", "duplicator", "eeff6b5f499f327db4c07a6612797232", "Total size and row counts are approximate values.  The thresholds that trigger notices are <i>%1$s OR %2$s</i> records total for the entire database.  Larger databases take more time to process.  On some budget hosts that have cpu/memory/timeout limits this may cause issues.", NULL, "LINE", NULL, "0", "", "3b00628cde1871c0e48e8e92f0fe3e43");
INSERT INTO `wp_icl_strings` VALUES("9879", "en", "duplicator", "d16406fe57a0b23b6e5989a94e154e80", "TABLE DETAILS:", NULL, "LINE", NULL, "0", "", "dac28239a504b7b896031bee7d8254eb");
INSERT INTO `wp_icl_strings` VALUES("9880", "en", "duplicator", "6fe28ec5c2aded912b709a1f70dc16c6", "The notices for tables are <i>%1$s, %2$s records or names with upper-case characters</i>.  Individual tables will not trigger a notice message, but can help narrow down issues if they occur later on.", NULL, "LINE", NULL, "0", "", "aaa98e11a9048e640dc3e86b20b129c2");
INSERT INTO `wp_icl_strings` VALUES("9881", "en", "duplicator", "b8dc0349754580582ec3ab2dbd60bd84", "RECOMMENDATIONS:", NULL, "LINE", NULL, "0", "", "090d60fda2d20820c06f6fbb4c524169");
INSERT INTO `wp_icl_strings` VALUES("9882", "en", "duplicator", "4d03175cd952ead052b9568515f9f4df", "repair and optimization", NULL, "LINE", NULL, "0", "", "fb8da4b3cfc3e72983a4d00c96526cf7");
INSERT INTO `wp_icl_strings` VALUES("9883", "en", "duplicator", "1d42632f5cfdc0128e6047a4b9f86f05", "1. Run a %1$s on the table to improve the overall size and performance.", NULL, "LINE", NULL, "0", "", "09a7ed4365f8c40a1adb7cc2b484592c");
INSERT INTO `wp_icl_strings` VALUES("9884", "en", "duplicator", "f59334adfc34dd64403e1f629a47952c", "2. Remove post revisions and stale data from tables.  Tables such as logs, statistical or other non-critical data should be cleared.", NULL, "LINE", NULL, "0", "", "9379772731851de38b196ef665057ff3");
INSERT INTO `wp_icl_strings` VALUES("9885", "en", "duplicator", "fc6415b8d8fd590e3309d224a2979656", "Enable mysqldump", NULL, "LINE", NULL, "0", "", "4712defc17608522314a8d4d3eff54c4");
INSERT INTO `wp_icl_strings` VALUES("9886", "en", "duplicator", "0f08c21cc2b512aa4bf9a1ad25e23811", "3. %1$s if this host supports the option.", NULL, "LINE", NULL, "0", "", "1573f9dd40b11094291b6fb8812a38da");
INSERT INTO `wp_icl_strings` VALUES("9887", "en", "duplicator", "41fdddc4932ea355a561fab539eeff56", "lower_case_table_names", NULL, "LINE", NULL, "0", "", "cf7ba4aef6ce9e7d82d06cd5f0be6dd5");
INSERT INTO `wp_icl_strings` VALUES("9888", "en", "duplicator", "e65267e8764068cd069a176950a4c3e4", "4. For table name case sensitivity issues either rename the table with lower case characters or be prepared to work with the %1$s system variable setting.", NULL, "LINE", NULL, "0", "", "0912be6a5d5e7a5c8817774e097c20ed");
INSERT INTO `wp_icl_strings` VALUES("9889", "en", "duplicator", "180a8a4d47a83a9f4b1b2fdefe5b180e", "Scan Details", NULL, "LINE", NULL, "0", "", "55090380abe134d5636b30c55062ba36");
INSERT INTO `wp_icl_strings` VALUES("9890", "en", "duplicator", "0f2664aaabff5e9422f9c29efaa77780", "Copy Quick Filter Paths", NULL, "LINE", NULL, "0", "", "b3b812a3c013bd473641ee8714469422");
